INSERT INTO authors (author_name, author_info, img_path)
VALUES
    ('Jane Austen', 'Jane Austen was an English novelist known for her classic works such as Pride and Prejudice and Sense and Sensibility.', 'Jane_Austen.jpg'),
    ('Harper Lee', 'Harper Lee was an American author best known for her novel To Kill a Mockingbird, which explores themes of racial injustice and morality.', 'Harper_Lee.jpg'),
    ('George Orwell', 'George Orwell was a British author and journalist known for his dystopian novels such as 1984 and Animal Farm, which offer social and political critiques.', 'George_Orwell.jpg'),
    ('F. Scott Fitzgerald', 'F. Scott Fitzgerald was an American writer known for his novel The Great Gatsby, a quintessential work of American literature that examines the Jazz Age and the pursuit of the American Dream.', 'F.Scott_Fitzgerald.jpg'),
    ('J.R.R. Tolkien', 'J.R.R. Tolkien was an English writer and academic who created the fantasy world of Middle-earth and wrote The Hobbit and The Lord of the Rings, beloved by readers worldwide.', 'J.R.R._Tolkien.jpg'),
    ('Paulo Coelho', 'Paulo Coelho is a Brazilian author known for his inspirational novels, including The Alchemist, which has become one of the best-selling books in history.', 'Paulo_Coelho.jpg'),
