UPDATE books b
    JOIN (
    SELECT book_id, ROUND(AVG(rating), 2) AS avg_rating
    FROM reviews
    GROUP BY book_id
    ) AS avg_ratings ON b.book_id = avg_ratings.book_id
    SET b.rating = avg_ratings.avg_rating;










